INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(1, 'Isabel', 'Allende', '1942-08-02', NULL, 'Cilena', 'Isabel Allende è una scrittrice cileno-americana. Le sue opere, spesso con elementi di realismo magico, includono "La casa degli spiriti" (1982).', 'Isabel.png');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(2, 'TJ', 'Klune', '1982-05-20', NULL, 'Americano', 'Travis John Klune è un autore americano di fantasy e narrativa romantica con protagonisti LGBTQ+.', 'Klune.jpeg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(3, 'Maurizio', 'De Giovanni', '1958-03-15', NULL, 'Italiano', 'Maurizio de Giovanni è nato a Napoli nel 1958, dove ha vissuto e lavorato per gran parte della sua vita. Le sue opere sono tradotte in molte lingue.', 'DeGiovanni.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(4, 'Rebecca', 'Ross', '1990-01-01', NULL, 'Americana', 'Rebecca Ross è un’autrice americana di romanzi fantasy per giovani adulti e adulti, nota per la duologia "Letters of Enchantment".', 'Rebecca.jpeg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(5, 'Benjamin', 'Stevenson', '1995-03-16', NULL, 'Australiano', 'Benjamin Stevenson è un comico e scrittore pluripremiato. Il suo romanzo "Everyone in My Family Has Killed Someone" diventerà una serie HBO.', 'Benjamin.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(6, 'Colleen', 'Hoover', '1979-12-11', NULL, 'Americana', 'Colleen Hoover è un’autrice americana di romanzi rosa e young adult, nota per "It Ends with Us". Ha venduto oltre 20 milioni di copie.', 'colleen.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(7, 'Felicia', 'Kingsley', '1987-10-13', NULL, 'Italiana', 'Felicia Kingsley, pseudonimo di Serena Artioli, è un’autrice italiana con oltre 1.000.000 di copie vendute. Le sue opere sono tradotte in inglese e ceco.', 'felicia.jpeg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(8, 'Ken', 'Follet', '1949-06-05', NULL, 'Britannico', 'Ken Follett è un autore gallese di thriller e romanzi storici, con oltre 195 milioni di copie vendute in tutto il mondo.', 'Ken.jpeg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(9, 'Dan', 'Brown', '1964-06-22', NULL, 'Americano', 'Dan Brown è un autore americano noto per i suoi thriller ricchi di crittografia, arte e teorie del complotto.', 'Dan.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(10, 'Abhijit V.', 'Banerjee', '1961-02-21', NULL, 'Indiano-Americano', 'Abhijit Banerjee è un economista indiano-americano, vincitore del Nobel per l’Economia 2019 per il suo approccio sperimentale alla lotta contro la povertà.', 'ab.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(11, 'Esther', 'Duflo', '1972-10-25', NULL, 'Franco-Americana', 'Esther Duflo è un’economista franco-americana, premio Nobel 2019 per i suoi studi sperimentali sulla riduzione della povertà.', 'esther.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(14, 'Emily', 'Wibberley', '1992-06-03', NULL, 'Americana', 'Emily Wibberley è un’autrice bestseller di narrativa romantica, nota per le sue collaborazioni con Austin Siegemund-Broka.', 'emily.jpg');
INSERT INTO author (id, name, surname, born_year, death_year, nationality, description, image_file_name) VALUES(15, 'Austin', 'Siegemund-Broka', '1991-09-18', NULL, 'Americano', 'Austin Siegemund-Broka è un autore ed ex giornalista che scrive romanzi rosa insieme a Emily Wibberley.', 'austin.jpg');
