INSERT INTO review (id, title, vote, text, book_id, credential_id, date) VALUES (21, 'Viaggio Emotivo', 5, 'Scritto magnificamente e profondamente emozionante. Un piccolo gioiello.', 13, 4, '18/06/2025');
