INSERT INTO review (id, title, vote, text, book_id, credential_id, date) VALUES (23, 'Fantasy Originale', 5, 'Uno dei romanzi fantasy più originali che abbia mai letto. Amato dall’inizio alla fine.', 4, 3, '18/06/2025');
