INSERT INTO review (id, title, vote, text, book_id, credential_id, date) VALUES (31, 'Toccante nel Profondo', 5, 'Una storia meravigliosamente scritta sull’unione umana e sul sentirsi a casa oltre i confini. Personaggi incredibilmente realistici.', 16, 2, '20/06/2025');
INSERT INTO review (id, title, vote, text, book_id, credential_id, date) VALUES (32, 'Viaggio Emotivo', 4, 'Esplora amore e amicizia attraverso le barriere culturali. Toccante, pieno di speranza, attuale.', 16, 3, '20/06/2025');
INSERT INTO review (id, title, vote, text, book_id, credential_id, date) VALUES (33, 'Teso e Romantico', 5, 'Un romance lento ma carico di tensione emotiva. Ho adorato la dinamica tra i due autori.', 18, 4, '20/06/2025');
INSERT INTO review (id, title, vote, text, book_id, credential_id, date) VALUES (34, 'Reale e Riconoscibile', 4, 'Arguto, agrodolce e onesto sul processo creativo e sull’amore. Perfetto per chi ama le seconde possibilità.', 18, 3, '20/06/2025');
